import 'package:cloud_firestore/cloud_firestore.dart';import 'package:firebase_auth/firebase_auth.dart';import 'package:flutter_riverpod/flutter_riverpod.dart';import '../services/firebase_service.dart';final firebaseServiceProvider = Provider<FirebaseService>((ref) {  return FirebaseService(); // FirebaseService의 인스턴스를 반환});// 현재 로그인된 사용자의 정보를 비동기적으로 가져오는 FutureProvider// FirebaseService의 `getCurrentUser` 메서드를 호출하여 현재 로그인된 사용자의 정보를 가져오는 비동기적인 작업을 처리한다.final currentUserProvider = FutureProvider<User?>((ref) async {  final firebaseService = ref.watch(firebaseServiceProvider);  return firebaseService.getCurrentUser(); // 현재 로그인된 사용자 정보를 반환});// 특정 사용자의 데이터를 Firestore에서 비동기적으로 가져오는 FutureProvider// 이 프로바이더는 `userId`를 파라미터로 받아서 Firestore에서 해당 사용자의 데이터를 가져온다.final userDataProvider = FutureProvider.family<DocumentSnapshot, String>((ref, userId) async {  final firebaseService = ref.watch(firebaseServiceProvider); //  return firebaseService.getUserData(userId); // Firestore에서 `userId`에 해당하는 사용자의 데이터를 가져온다.});