import 'package:flutter/material.dart';import 'package:flutter_riverpod/flutter_riverpod.dart';import 'package:go_router/go_router.dart';import 'package:social_login_buttons/social_login_buttons.dart';import '../providers/login_providers.dart';class LoginScreen extends ConsumerWidget {  @override  Widget build(BuildContext context, WidgetRef ref) {    final loginState = ref.watch(loginProvider);    final emailController = TextEditingController();    final passwordController = TextEditingController();    return Scaffold(      appBar: AppBar(        title: const Text('Login'),      ),      body: Padding(        padding: const EdgeInsets.all(16.0),        child: Column(          mainAxisAlignment: MainAxisAlignment.center,          children: <Widget>[            TextField(              controller: emailController,              decoration: const InputDecoration(labelText: 'Email'),            ),            TextField(              controller: passwordController,              obscureText: true,              decoration: const InputDecoration(labelText: 'Password'),            ),            const SizedBox(height: 40),            loginState.isLoading                ? const CircularProgressIndicator() // 로딩 중이면 로딩 표시                : SocialLoginButton(                    backgroundColor: Colors.white,                    height: 50,                    text: 'Login with Email',                    textColor: Colors.black,                    fontSize: 20,                    buttonType: SocialLoginButtonType.generalLogin,                    onPressed: () {                      ref.read(loginProvider.notifier).login(                            emailController.text,                            passwordController.text,                            context,                          );                    },                  ),            const SizedBox(height: 20),            SocialLoginButton(              onPressed: () {                // Google 로그인 구현 (나중에 추가 가능)              },              buttonType: SocialLoginButtonType.google,            ),            const SizedBox(height: 20),            const Divider(),            SocialLoginButton(              backgroundColor: Colors.white,              height: 50,              text: 'Sign Up with Email',              textColor: Colors.black,              fontSize: 20,              buttonType: SocialLoginButtonType.generalLogin,              onPressed: () {                GoRouter.of(context).push('/Signup');              },            ),          ],        ),      ),    );  }}